function showOneDivHideOthers(ShowDiv, HideDiv)
{
   var mydiv2 = document.getElementById(ShowDiv);
   mydiv2.style.visibility="";
   mydiv2.style.display="";

   var mydiv1 = document.getElementById(HideDiv);
   mydiv1.style.visibility="";
   mydiv1.style.display="none";
}

